/* Container with logo background */
.department-banner .slds-p-vertical_medium.slds-p-horizontal_large {
  height: 280px !important; /* Total height for logo + content */
  background-size: 180px 50px !important; /* Control logo size */
  background-position: top left !important;
  background-repeat: no-repeat !important;
  padding: 60px 0 0 0 !important; /* Push content below logo */
  position: relative;
  z-index: 1;
}


/* Home page department IBM banners */
.department-banner {
  background-color: white !important;
  padding: 1.5rem !important;
  position: relative;
  overflow: hidden !important; /* Prevent background bleed */
}

/* Main container */
.department-banner .slds-p-vertical_medium.slds-p-horizontal_large {
  height: auto !important;
  background-size: 180px 50px !important;
  background-position: top left !important;
  background-repeat: no-repeat !important;
  padding: 60px 0 0 0 !important;
  position: relative;
  z-index: 1;
  background-color: white !important;
}

/* White overlay to prevent bleed-through */
.department-banner .slds-p-vertical_medium.slds-p-horizontal_large::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: white;
  z-index: -1;
}

/* Force background image to only show in logo area */
.department-banner .slds-p-vertical_medium.slds-p-horizontal_large::after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 180px;
  height: 50px;
  background: inherit;
  background-size: contain !important;
  background-position: top left !important;
  background-repeat: no-repeat !important;
  z-index: 2;
}

/* Hide original background */
.department-banner .x7s-banner-overlay {
  display: none !important;
  opacity: 0 !important;
}

.department-banner .slds-text-heading_medium {
  font-size: 1.125rem;
  color: #1a1a1a;
  margin-bottom: 0.75rem;
  font-weight: 500;
}

.department-banner .x7s-banner__text p {
  font-size: 0.875rem;
  color: #444;
  line-height: 1.6;
  margin-bottom: 1.5rem;
  max-width: 42rem;
  position: relative;
  z-index: 1;
  background: white;
}

.department-banner .x7s-banner__buttons {
  margin-top: 1.5rem;
  position: relative;
  z-index: 1;
}

.department-banner .buttons-horizontal {
  display: flex;
  gap: 1.5rem;
  align-items: center;
}
